INSERT INTO `bo1e_options` VALUES (6632,'wp_affiliate_platform_configs','s:2319:\"a:20:{s:32:\"wp_aff_using_wp_hash_pass_system\";s:1:\"1\";s:28:\"wp_affiliate_debug_file_name\";s:105:\"/home/content/19/7421519/html/wp-draft1-s/wp-content/plugins/wp-affiliate-platform/wp_affiliate_debug.log\";s:18:\"wp_aff_index_title\";s:27:\"Welcome to Affiliate Center\";s:17:\"wp_aff_index_body\";s:969:\"Join our affiliate program and start earning money for every sale you send our way! Simply create your account, place your linking code into your website and watch your account balance grow as your visitors become our customers.\r\n&lt;h3&gt;How Does it Work?&lt;/h3&gt;\r\nThe process is very simple:\r\n\r\n1. Visitor clicks on an affiliate link on your site or in an email.\r\n\r\n2. The visitors IP is logged and a cookie is placed in their browser for tracking purposes.\r\n\r\n3. The visitor browses our site, and may decide to order.\r\n\r\n4. If the visitor orders (the order does not need to be placed during the same browser session - cookies and IPs are stored up to a configurable amount of time), the order will be registered as a sale for you and you will receive commission for this sale.\r\n&lt;h3&gt;Already an Affiliate?&lt;/h3&gt;\r\nPlease visit the Affiliate Login page and enter your username and password to gain access to your account statistics, banners, linking code.\";s:33:\"wp_aff_comm_notif_senders_address\";s:34:\"From Now On <vadhami@fromnowon.ca>\";s:31:\"wp_aff_comm_notif_email_subject\";s:23:\"Commission Notification\";s:28:\"wp_aff_comm_notif_email_body\";s:0:\"\";s:29:\"wp_aff_do_not_show_sc_warning\";s:0:\"\";s:34:\"wp_aff_notify_admin_for_commission\";s:1:\"1\";s:37:\"wp_aff_admin_comm_notif_email_subject\";s:33:\"Affiliate commission notification\";s:34:\"wp_aff_admin_comm_notif_email_body\";s:197:\"Vittoria, this is an auto-generated email letting you know that one of your affiliates has earned a commission. You can log into your web site dashboard and get more details about this transaction.\";s:36:\"wp_aff_enable_manual_signup_approval\";s:0:\"\";s:33:\"wp_aff_make_paypal_email_required\";s:0:\"\";s:24:\"wp_aff_hide_tax_id_field\";s:1:\"1\";s:32:\"wp_aff_enable_registration_bonus\";s:0:\"\";s:29:\"wp_aff_registration_bonus_amt\";s:0:\"\";s:44:\"wp_aff_show_buyer_details_name_to_affiliates\";s:0:\"\";s:32:\"wp_aff_show_txn_id_to_affiliates\";s:0:\"\";s:37:\"wp_aff_do_not_show_powered_by_section\";s:0:\"\";s:23:\"wp_aff_welcome_page_msg\";s:0:\"\";}\";','yes');
