INSERT INTO `bo1e_posts` VALUES (433,2,'2012-01-05 17:14:56','2012-01-05 22:14:56','From Now On services are available throughout the greater Toronto area.\r\n\r\nUnderstanding the challenges of day-to-day life, our services are available to you at your convenience – our hours are flexible and we will work with you to ensure that your needs are met within the time you have available.\r\n\r\nPlease contact us directly to make arrangements for a consultation. We are available to answer any questions you may have about our services.\r\n\r\nWhen contacting us directly, email is preferred so please use the form below.\r\n\r\nAlternatively, we can be reached by phone at 647.477.3372.\r\n\r\nEnquiries can also be directed via facsimile: 416.619.0045\r\n\r\n[contactform email=\"vadhami@fromnowon.ca\"]','Contact Us','','inherit','open','open','','35-revision-4','','','2012-01-05 17:14:56','2012-01-05 22:14:56','',35,'http://fromnowon.ca/35-revision-4.html',0,'revision','',0);
